mapper.update:
UPDATE emp SET
ename = #{ename}, deptno = #{deptno}, role_num = #{role_num}, sal = #{sal},
work_status = #{work_status}, hiredate = #{hiredate}, resign_date = #{resign_DATE},
mgr = #{mgr}, email = #{email}, phone = #{phone}, username = #{username},
password = #{password}, posname = #{posname}
WHERE empno = #{empno}

mapper.insert:
